    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        try{
            javax.swing.table.DefaultTableModel d=(javax.swing.table.DefaultTableModel)tb1.getModel();
            d.setRowCount(0);

            Class.forName("java.sql.DriverManager");
            java.sql.Connection c= java.sql.DriverManager.getConnection("jdbc:mysql://localhost:3306/geetanshu","root","root");
            java.sql.Statement s=c.createStatement();
            java.sql.ResultSet r=s.executeQuery("select *from record;");

            while(r.next()){

                String id=r.getInt(1)+"";
                String name=r.getString(2);
                String medicine=r.getString(3)+"";
                String quantity=r.getInt(4)+"";
                String amount=r.getInt(5)+"";
                

                d.addRow(new String[]{id,name,medicine,quantity,amount});
            }
        }catch(Exception e)
        {System.out.println(e);
        }

    }//GEN-LAST:event_jButton1ActionPerformed

